(1, 1, '一位WordPress评论者', 'wapuu@wordpress.example', 'https://wordpress.org/', '', 0x323031372d30342d32302031343a32333a3439, 0x323031372d30342d32302030363a32333a3439, '嗨，这是一条评论。\n要开始审核、编辑及删除评论，请访问仪表盘的“评论”页面。\n评论者头像来自<a href=\"https://gravatar.com\">Gravatar</a>。', 0, '1', '', '', 0, 0),
(2, 155, 'admin', '', '', '::1', 0x323031372d30392d30372031343a35313a3337, 0x323031372d30392d30372030363a35313a3337, '从此在心田恣意流淌。', 0, '1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/59.0.3071.115 Safari/537.36', '', 0, 1),
(3, 155, 'admin', '', '', '::1', 0x323031372d30392d30372031343a35313a3435, 0x323031372d30392d30372030363a35313a3435, '伴随着秋水天长，', 0, '1', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/59.0.3071.115 Safari/537.36', '', 0, 1);
